/*Table structure for table `role` */

DROP TABLE IF EXISTS `role`;

CREATE TABLE `role` (
  `role_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '角色id',
  `role_name` varchar(100) DEFAULT NULL COMMENT '角色名称',
  `role_info` varchar(100) DEFAULT NULL COMMENT '角色描述',
  PRIMARY KEY (`role_id`)
) ENGINE=InnoDB AUTO_INCREMENT=7 DEFAULT CHARSET=utf8;

/*Data for the table `role` */

insert  into `role`(`role_id`,`role_name`,`role_info`) values (1,'deptAdmin','部门管理'),(2,'jobAdmin','职务管理'),(3,'empAdmin','员工管理'),(4,'anaAdmin','数据分析管理'),(5,'searchAdmin','数据检索管理'),(6,'picAdmin','图库管理');

/*Table structure for table `role_permission` */

DROP TABLE IF EXISTS `role_permission`;

CREATE TABLE `role_permission` (
  `rp_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键自动编号',
  `role_id` int(11) DEFAULT NULL COMMENT '角色',
  `perm_id` int(11) DEFAULT NULL COMMENT '权限',
  PRIMARY KEY (`rp_id`),
  KEY `rp_role_id_fk` (`role_id`),
  KEY `rp_perm_id_fk` (`perm_id`),
  CONSTRAINT `rp_perm_id_fk` FOREIGN KEY (`perm_id`) REFERENCES `permission` (`perm_id`) ON DELETE CASCADE,
  CONSTRAINT `rp_role_id_fk` FOREIGN KEY (`role_id`) REFERENCES `role` (`role_id`) ON DELETE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=21 DEFAULT CHARSET=utf8;

