/*Table structure for table `user_role` */

DROP TABLE IF EXISTS `user_role`;

CREATE TABLE `user_role` (
  `ur_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '主键自动编号',
  `user_id` int(11) DEFAULT NULL COMMENT '用户',
  `role_id` int(11) DEFAULT NULL COMMENT '角色',
  PRIMARY KEY (`ur_id`),
  KEY `ur_user_id_fk` (`user_id`),
  KEY `ur_role_id_fk` (`role_id`),
  CONSTRAINT `ur_role_id_fk` FOREIGN KEY (`role_id`) REFERENCES `role` (`role_id`) ON DELETE CASCADE,
  CONSTRAINT `ur_user_id_fk` FOREIGN KEY (`user_id`) REFERENCES `emp` (`emp_id`) ON DELETE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=13 DEFAULT CHARSET=utf8;

